import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';
import React from 'react';
import { format } from '@jd/jmtd-tools/es/number';
import {
    Summary,
    SummaryCardTitle,
    SummaryCardIndex,
    SummaryCardDescription,
    ColorSummary
} from '@jd/jmtd-pro';
import '@jd/jmtd-pro/dist/themes/default.css';


const data = [
    {
        name: '指标名称',
        description: '指标说明',
        index: 12345,
        circle: 0.23
    },

    {
        name: '指标名称',
        description: '指标说明',
        index: 41235,
        circle: -0.43
    },

    {
        name: '指标名称',
        description: '指标说明',
        index: 4123,
        circle: 0
    }
];

function SummaryRender({
    name, description, index, circle
}) {
    return (
        <>
            <SummaryCardTitle info={description}>{name}</SummaryCardTitle>
            <SummaryCardIndex>{format(index, 'number_0_0')}</SummaryCardIndex>
            <SummaryCardDescription
                left='环比'
                right={circle ? format(Math.abs(circle), 'percent_2_0_1') : '--'}
                ascend={circle > 0}
                descend={circle < 0}
            />
        </>
    );
}

SummaryRender.propTypes = {
    circle: PropTypes.number.isRequired,
    description: PropTypes.string.isRequired,
    index: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired
};

function App() {
    return (
        <Summary
            data={data}
            render={ColorSummary}
            itemRender={SummaryRender}
            cardProps={({ circle }) => ({
                ascend: circle > 0,
                descend: circle < 0
            })}
        />
    );
}

export default App;

